import { useState } from 'react';
import './App.css';
import Navbar from "./comp/Navbar";
import TextForm from "./comp/TextForm";
import Alert from "./comp/Alert";
import About from "./comp/About";
// import {
//   BrowserRouter as Router,
//   Switch,
//   Route,
