          <Routes>
          <Route path="/"><TextForm showAlert={showAlert} heading="Enter text below" mode={mode} /></Route>
          <Route path="/About"><About/></Route>
          </Routes>
        </div>
      </Router > */}
    </>
  );
}

export default App;
